sys_script_fix.script:
var applicationRequirementGR = new GlideRecord("x_icir_dealmaker_application_requirement");
applicationRequirementGR.query();
while (applicationRequirementGR.next()) {

		var application = String(applicationRequirementGR.getValue("application"));
		var applicationRequirement = String(applicationRequirementGR.getUniqueValue());

		var joinGR = new GlideRecord("x_icir_dealmaker_m2m_app_requirement_app");
		joinGR.initialize();
		joinGR.application = application;
		joinGR.application_requirement = applicationRequirement;
		joinGR.insert();
}



var applicationGR = new GlideRecord("x_icir_dealmaker_application");
applicationGR.query();
while (applicationGR.next()) {

		var applicationSuite = String(applicationGR.getValue("application_suite"));
		var application = String(applicationGR.getUniqueValue());

		var joinGR = new GlideRecord("x_icir_dealmaker_m2m_app_app_suite");
		joinGR.initialize();
		joinGR.application_suite = applicationSuite;
		joinGR.application = application;
		joinGR.insert();
}
