sys_ui_action.script:
function copyExcelEstimate() {

	// Generate random string for Div Name (so multiple divs with the same ID do not accumulate when switching b/e records)
	var result = '';  
	var characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';  
	var charactersLength = characters.length;  
	for ( var i = 0; i < 10; i++ ) {  
		result += characters.charAt(Math.floor(Math.random() * charactersLength));  
	}  


	// Declare DOM scope objects
	var document = top.document;
	var window = top.window;

	var mydiv = result;
	var test = document.createElement("DIV");
	test.setAttribute("id", mydiv);
	test.innerHTML = g_form.getValue("estimate_table_excel");
	var t = test.firstChild;
	test.appendChild(t);
	document.body.appendChild(test);
	if (document.selection) {
		var range = document.body.createTextRange();
		range.moveToElementText(document.getElementById(mydiv));
		range.select().createTextRange();
		document.execCommand("Copy");
	}

	else if (window.getSelection) {
		var range1 = document.createRange();
		document.getElementById(mydiv).style.display = "block";
		range1.selectNode(document.getElementById(mydiv));
		var selection = window.getSelection();
		selection.removeAllRanges(); // Remove all ranges from the selection.
		selection.addRange(range1);
		document.execCommand("Copy");
		document.getElementById(mydiv).style.display = "none";
	}

	g_form.addInfoMessage('Excel-Ready Estimate has been copied to your clipboard.');
}
